    box-shadow: 0 0 20px rgba(244, 196, 48, 0.5);
  }

  .nav-blur {
    background: rgba(255, 248, 240, 0.8);
    backdrop-filter: blur(20px);
    border-bottom: 1px solid rgba(244, 196, 48, 0.2);
  }

  .dark .nav-blur {
    background: rgba(8, 8, 8, 0.9);
    border-bottom: 1px solid rgba(244, 196, 48, 0.3);
  }

  .hero-gradient {
    background: linear-gradient(135deg, 
      hsl(var(--background)) 0%, 
      rgba(244, 196, 48, 0.1) 50%, 
      hsl(var(--background)) 100%);
  }

  .dark .hero-gradient {
    background: linear-gradient(135deg, 
      hsl(var(--background)) 0%, 
      rgba(244, 196, 48, 0.05) 50%, 
      hsl(var(--background)) 100%);
  }

  .product-card:hover {
    @apply transform -translate-y-2 scale-105;
    transition: all 0.3s ease;
  }

  .product-card:hover .product-image {
    @apply transform scale-110;
    transition: transform 0.3s ease;
  }
}

@layer utilities {
  .animate-sparkle {
    animation: sparkle 3s ease-in-out infinite;
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-shimmer {
    animation: shimmer 2s ease-in-out infinite alternate;
  }

  .animate-heartbeat {
    animation: heartbeat 1.5s ease-in-out infinite;
  }

  .animate-confetti-fall {
    animation: confetti-fall 3s linear infinite;
  }

  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }
}

@keyframes sparkle {
  0%, 100% { 
    opacity: 0; 
    transform: scale(0.8) rotate(0deg); 
  }
  50% { 
    opacity: 1; 
    transform: scale(1.2) rotate(180deg); 
  }
}

@keyframes float {
  0%, 100% { 
    transform: translateY(0px); 
  }
  50% { 
    transform: translateY(-20px); 
  }
}

@keyframes shimmer {
  0% { 
    opacity: 0.5; 
  }
  100% { 
    opacity: 1; 
  }
}

@keyframes heartbeat {
  0%, 100% { 
    transform: scale(1); 
  }
  50% { 
    transform: scale(1.1); 
  }
}

@keyframes confetti-fall {
  0% { 
    transform: translateY(-100vh) rotate(0deg); 
    opacity: 1; 
  }
  100% { 
    transform: translateY(100vh) rotate(360deg); 
    opacity: 0; 
  }
}

@keyframes glow {
  0% { 
    box-shadow: 0 0 20px rgba(244, 196, 48, 0.5); 
  }
  100% { 
    box-shadow: 0 0 30px rgba(244, 196, 48, 0.8); 
  }
}

